// 评估页面图表title设置
function getTitleConfig (title) {
  return {
    show: !!title,
    text: title,
    x: 'center',
    y: 'bottom',
    textStyle: {
      color: '#666',
      fontSize: 16
    },
    backgroundColor: 'rgba(236, 169, 114, 0.212)',
    borderColor: 'rgb(240, 145, 45)',
    borderWidth: 1,
    borderRadius: 14,
    padding: [8, 50, 8, 50]
  }
}

